import React, { useEffect, useState } from "react";

const offers = [
  {
    id: 1,
    title: "Hamilton - 30% Off",
    location: "Richard Rodgers Theatre",
    originalPrice: "$150",
    discountedPrice: "$105",
    image:
      "data:image/jpeg;base64,",
    expiry: "2025-11-15T23:59:59",
  },
  {
    id: 2,
    title: "Wicked - Flash Sale",
    location: "Gershwin Theatre",
    originalPrice: "$120",
    discountedPrice: "$90",
    image:
      "data:image/jpeg;base64,",
    expiry: "2025-11-10T23:59:59",
  },
  {
    id: 3,
    title: "Aladdin - Special Offer",
    location: "Minskoff Theatre",
    originalPrice: "$130",
    discountedPrice: "$95",
    image:
      "data:image/jpeg;base64,",
    expiry: "2025-11-12T23:59:59",
  },
];

const LimitedTimeOffers = () => {
  const [timers, setTimers] = useState({});

  useEffect(() => {
    const interval = setInterval(() => {
      const newTimers = {};
      offers.forEach((offer) => {
        const now = new Date().getTime();
        const expiry = new Date(offer.expiry).getTime();
        const distance = expiry - now;

        if (distance > 0) {
          const days = Math.floor(distance / (1000 * 60 * 60 * 24));
          const hours = Math.floor(
            (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
          );
          const minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
          const seconds = Math.floor((distance % (1000 * 60)) / 1000);
          newTimers[offer.id] = { days, hours, minutes, seconds };
        } else {
          newTimers[offer.id] = null; // Expired
        }
      });
      setTimers(newTimers);
    }, 1000);

    return () => clearInterval(interval);
  }, []);

  return (
    <section className="bg-gray-100 dark:bg-gray-900 py-12 px-6">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-2xl md:text-3xl font-bold text-gray-900 dark:text-gray-100 mb-8 text-center">
          ‚è∞ Limited Promo Offers
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {offers.map((offer) => (
            <div
              key={offer.id}
              className="bg-white dark:bg-gray-800 rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300 overflow-hidden flex flex-col"
            >
              
              <div className="h-48 w-full overflow-hidden">
                <img
                  src={offer.image}
                  alt={offer.title}
                  className="w-full h-full object-cover"
                />
              </div>

            
              <div className="p-4 flex flex-col flex-1">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100 mb-1">
                  {offer.title}
                </h3>
                <p className="text-sm text-gray-500 dark:text-gray-400 mb-2">
                  {offer.location}
                </p>
                <div className="flex items-center gap-2 mb-2">
                  <span className="text-red-500 font-bold">
                    {offer.discountedPrice}
                  </span>
                  <span className="line-through text-gray-400">
                    {offer.originalPrice}
                  </span>
                </div>

                
                {timers[offer.id] ? (
                  <p className="text-sm text-gray-700 dark:text-gray-300 mb-4">
                    Ends in:{" "}
                    {timers[offer.id].days}d {timers[offer.id].hours}h{" "}
                    {timers[offer.id].minutes}m {timers[offer.id].seconds}s
                  </p>
                ) : (
                  <p className="text-sm text-gray-700 dark:text-gray-300 mb-4 font-semibold">
                    Offer expired
                  </p>
                )}

                <button className="mt-auto px-4 py-2 bg-red-500 hover:bg-red-700 text-white rounded-md text-sm transition-colors">
                  Book Now
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default LimitedTimeOffers;
